import React, { createContext } from 'react';
import axios from 'axios';
import {Form} from 'antd';

const BASE_URL = "https://localhost:44352/api/Account";

export default function LoginApi() {
  const handleSubmit =(event) => {
    event.preventDefault();
    const data = {
      email: event.target.email.value,
      password: event.target.password.value
    }
    axios.post(`${BASE_URL}/Login`,data)
      .then(response => {
        if (response.status === "200")
          window.location.href = "/fuel-wise";
      })
      .catch(err => console.log(err.response));
      }
      const handleChange = (event) => {
        event.preventDefault();
        const data = {
          remeber_me: event.target.remeber_me.value
        }
      }  
  return (
    <form onSubmit={handleSubmit} className="login">
      <div className="login">
        <div className="base-container">
          <div className="header">TransportWise</div>
          <div className="header2">Login</div>
          <div className="image">
            <img src="/static/images/town.png" />
          </div>
          <div className="content">
            <div className="form">
              <div className="form-group">
                <label htmlFor="email">Username</label>
                <input type="text" id="email" placeholder="Enter Email" />
              </div>
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" id="password" placeholder="Enter Password" />
              </div>
              <div className="form-check">
                <Form></Form>
            
              </div>
            </div>
          </div>
          <div className="footer">
            <button type="Submit" className="btnAll" onClick={handleSubmit}>
              Login
            </button>
            <br />
            <a href="/ForgotPassword" className="anchorFG">Forgot your password?</a>
            <br /> <br />
            <a href="/register" className="anchor">If you don't have an account, Register here.</a>
          </div>
        </div>
      </div>
    </form>
  );
}
