import React, { useContext, useState } from 'react';
import axios from 'axios';

const BASE_URL = "https://localhost:44352/api";

const EditPost = () => {
    const [Post, setPost] = useState([])

    const handleTitleChange = (event) => {
        setPost({ ...Post, title: event.target.title.value })
    }

    const handleContentChange = (event) => {
        setPost({ ...Post, content: event.target.content.value })
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        const data = {
            title: event.target.title.value,
            content: event.target.content.value,
            pid: props.location.state.post.post.pid
        }
        axios.put(`${BASE_URL}/Articles`, data)
            .then(response => console.log(response))
            .catch((err) => console.log(err.response))
    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label htmlFor="title">Article Title:</label> <br />
                <input type="text" id="title" placeholder="Enter Article title" value={Post.title} onChange={handleTitleChange} />
                <br />
                <br />
                <label htmlFor="content">Article content:</label> <br />
                <input className="input2" type="text" id="content" placeholder="Enter Article Content" value={Post.content} onChange={handleContentChange} />
                <br />
                <button type='submit'> Submit </button>
            </form>
            {/* <button onClick={() => history.replace('/posts')}> Cancel </button> */}
        </div>
    );
}

export default EditPost;
