import React, { FC, useState } from 'react';
import 'antd/dist/antd.css';
import { Form, Input, Button, } from 'antd';
import { useMutate } from 'restful-react';

interface IProps {
    readonly form?: any;
  }
  

const RegisterAPI: FC<IProps> = (props) => {
  
  // const [username, setUsername] = useState('');
  // const [password, setPassword] = useState('');
  // const [confirmpassword, setConfirmpassword] = useState('');
    const handleSubmit = () => {
      const { mutate: loginUserHttp } = useMutate({ verb: 'POST', path: 'https://localhost:44352/api/Account/Register' });

      loginUserHttp({
        email,
        password,
        confirmpassword
      })
        .then(data => console.log(data))
        .catch(err => console.log(err.data))
    }  
    const { getFieldDecorator } = props.form;
    const formItemLayout = {
        labelCol: {
          xs: { span: 24 },
          sm: { span: 8 },
        },
        wrapperCol: {
          xs: { span: 24 },
          sm: { span: 16 },
        },
      };
      const tailFormItemLayout = {
        wrapperCol: {
          xs: {
            span: 24,
            offset: 0,
          },
          sm: {
            span: 16,
            offset: 8,
          },
        },
      };
    return (
        <Form {...formItemLayout} onSubmit={handleSubmit}>
        <Form.Item label="E-mail">
          {getFieldDecorator('email', {
            rules: [
              {
                type: 'email',
                message: 'The input is not valid E-mail!',
              },
              {
                required: true,
                message: 'Please input your E-mail!',
              },
            ],
          })(<Input
            // value={username}
            // onChange={e => setUsername(e.target.value)}
          />)}
        </Form.Item>
        <Form.Item label="Password" hasFeedback>
          {getFieldDecorator('password', {
            rules: [
              {
                required: true,
                message: 'Please input your password!',
              },
            ],
          })(<Input.Password 
            // value={password}
            // onChange={e => setPassword(e.target.value)}
          />)}
        </Form.Item>
        <Form.Item label="Confirm Password" hasFeedback>
          {getFieldDecorator('confirm', {
            rules: [
              {
                required: true,
                message: 'Please confirm your password!',
              },
            ],
          })(<Input.Password 
            // value={confirmpassword}
            // onChange={e => setConfirmpassword(e.target.value)}
          />)}
        </Form.Item>
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit">
            Register
          </Button>
        </Form.Item>
      </Form>
    );
}
const WrappedRegisterAPI = Form.create({ name: 'register_api' })(RegisterAPI);
export default WrappedRegisterAPI;
