import React, { useContext, useState } from 'react';
import axios from 'axios';

const BASE_URL = "https://localhost:44352/api/Account";

export default function LoginApi() {
  const handleSubmit =(event) => {
    event.preventDefault();
    const data = {
      email: event.target.value.value,
      password: event.target.value.value,
      rememberMe: event.target.value.value
    }
    
    axios.post(`${BASE_URL}/Login`,data)
      .then(response => {
        if (response.status === "200")
          window.location.href = "/fuel-wise";
      })
      .catch(err => console.log(err.response));
      } 
      const [isChecked, setIsChecked] = useState(false);
  return (
    <form onSubmit={handleSubmit} className="login">
      <div className="login">
        <div className="base-container">
          <div className="header">TransportWise</div>
          <div className="header2">Login</div>
          <div className="image">
            <img src="/static/images/town.png" />
          </div>
          <div className="content">
            <div className="form">
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input type="text" id="email" placeholder="Enter Email" value={isChecked} onChange={() => setIsChecked(!isChecked)}/>
              </div>
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" id="password" placeholder="Enter Password" />
              </div>
              <div className="form-check">
            <input type="checkbox" id="rememberMe" value={isChecked} onChange={() => setIsChecked(!isChecked)}/>
            <label htmlFor="rememberMe">Remember me</label>
              </div>
            </div>
          </div>
          <div className="footer">
            <button type="Submit" className="btnAll" onClick={handleSubmit}>Login</button>
            <br />
            <a href="/ForgotPassword" className="anchorFG">Forgot your password?</a>
            <br /> <br />
            <a href="/register" className="anchor">If you don't have an account, Register here.</a>
          </div>
        </div>
      </div>
    </form>
  );
}
